package pacificsoft.pscomandera.Modelo;import java.io.Serializable;/** * Created by desarrollo ORM on image_nav_header_default_comanda/3/2018. */public class Usuario implements Serializable {    private int id = 0;    private String usuario, pass, codigo;    private boolean admin = false;    private int foto;    private String estatus;    private String tipo;    private String nombre;    private String codigoUsuario;    public Usuario() {    }    public Usuario(int id, String codigo, String usuario, String pass, int foto, boolean admin, String Estatus, String Tipo, String Nombre) {        this.codigo = codigo;        this.usuario = usuario;        this.pass = pass;        this.foto = foto;        this.admin = admin;        this.id = id;        this.estatus = Estatus;        this.tipo = Tipo;        this.nombre = Nombre;    }    public Usuario(Usuario us) {        this.setId( us.getId() );        this.setCodigo( us.getCodigo() );        this.setUsuario( us.getUsuario() );        this.setPass( us.getPass() );        this.setFoto( us.getFoto() );        this.setAdmin( us.isAdmin() );    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public boolean isAdmin() {        return admin;    }    public void setAdmin(boolean admin) {        this.admin = admin;    }    public String getCodigo() {        return codigo;    }    public void setCodigo(String codigo) {        this.codigo = codigo;    }    public int getFoto() {        return foto;    }    public void setFoto(int foto) {        this.foto = foto;    }    public String getPass() {        return pass;    }    public void setPass(String pass) {        this.pass = pass;    }    public String getUsuario() {        return usuario;    }    public void setUsuario(String usuario) {        this.usuario = usuario;    }    public String getEstatus() {        return estatus;    }    public void setEstatus(String estatus) {        this.estatus = estatus;    }    public String getTipo() {        return tipo;    }    public void setTipo(String tipo) {        this.tipo = tipo;    }    public String getNombre() {        return nombre;    }    public void setNombre(String nombre) {        this.nombre = nombre;    }}